CREATE TABLE T_customers( customer_id VARCHAR(100), customer_unique_id TEXT, customer_zip_code_prefix VARCHAR(100), PRIMARY KEY (customer_id), FOREIGN KEY (customer_zip_code_prefix) REFERENCES T_geolocation(geolocation_zip_code_prefix));
CREATE TABLE T_geolocation( geolocation_zip_code_prefix VARCHAR(100), geolocation_city TEXT, geolocation_state TEXT, PRIMARY KEY (geolocation_zip_code_prefix));
CREATE TABLE T_order_reviews( review_id TEXT, order_id TEXT, review_score TEXT, review_comment_title TEXT, review_comment_message TEXT, FOREIGN KEY (order_id) REFERENCES T_orders(order_id));
CREATE TABLE T_sellers( seller_id VARCHAR(100), seller_zip_code_prefix VARCHAR(100), PRIMARY KEY (seller_id), FOREIGN KEY (seller_zip_code_prefix) REFERENCES T_geolocation(geolocation_zip_code_prefix));
CREATE TABLE T_order_items( order_id TEXT, order_item_id TEXT, product_id TEXT, seller_id TEXT, shipping_limit_date TEXT, price TEXT, freight_value TEXT, FOREIGN KEY (order_id) REFERENCES T_orders(order_id), FOREIGN KEY (seller_id) REFERENCES T_sellers(seller_id), FOREIGN KEY (product_id) REFERENCES T_products(product_id));
CREATE TABLE T_order_payments( order_id VARCHAR(100), payment_sequential TEXT, payment_type TEXT, payment_installments TEXT, payment_value TEXT, FOREIGN KEY (order_id) REFERENCES T_orders(order_id));
CREATE TABLE T_orders( order_id VARCHAR(100), customer_id VARCHAR(100), order_status TEXT, order_purchase_timestamp TEXT, order_approved_at TEXT, order_delivered_carrier_date TEXT, order_delivered_customer_date TEXT, order_estimated_delivery_date TEXT, PRIMARY KEY (order_id), FOREIGN KEY (customer_id) REFERENCES T_customers(customer_id));
CREATE TABLE T_products( product_id VARCHAR(100), product_category_name TEXT, product_name_lenght TEXT, product_description_lenght TEXT, product_photos_qty TEXT, product_weight_g TEXT, product_length_cm TEXT, product_height_cm TEXT, product_width_cm TEXT, PRIMARY KEY (product_id));
